import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Alert,
  ActivityIndicator,
  StyleSheet,
  SafeAreaView,
  Image,
  Linking,
} from 'react-native';
import { getAuth, updatePassword, signOut } from 'firebase/auth';
import { getDatabase, ref, get, set } from 'firebase/database';
import { ref as storageRef, uploadBytes, getDownloadURL } from 'firebase/storage';
import * as ImagePicker from 'expo-image-picker';
import { database, storage } from '../config/firebase';

const AccountScreen = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const [uploadingImage, setUploadingImage] = useState(false);

  useEffect(() => {
    const auth = getAuth();
    const currentUser = auth.currentUser;
    
    if (!currentUser) {
      navigation.navigate('Login');
      return;
    }

    const userRef = ref(database, `users/${currentUser.uid}`);
    get(userRef).then((snapshot) => {
      if (snapshot.exists()) {
        setUsername(snapshot.val().username || '');
        setProfileImage(snapshot.val().profileImage);
      }
      setLoading(false);
    });
  }, [navigation]);

  const handleUpdateProfile = async () => {
    try {
      setUpdating(true);
      const auth = getAuth();
      const currentUser = auth.currentUser;
      
      if (newPassword && confirmPassword) {
        if (newPassword !== confirmPassword) {
          Alert.alert('Hata', 'Şifreler eşleşmiyor');
          return;
        }
        if (newPassword.length < 6) {
          Alert.alert('Hata', 'Şifre en az 6 karakter olmalıdır');
          return;
        }
        await updatePassword(currentUser, newPassword);
      }

      const userRef = ref(database, `users/${currentUser.uid}`);
      await set(userRef, {
        email: currentUser.email,
        username: username,
        profileImage: profileImage,
      });

      Alert.alert('Başarılı', 'Profil güncellendi');
      setNewPassword('');
      setConfirmPassword('');
    } catch (error) {
      Alert.alert('Hata', error.message);
    } finally {
      setUpdating(false);
    }
  };

  const handleLogout = async () => {
    try {
      const auth = getAuth();
      await signOut(auth);
      navigation.navigate('Login');
    } catch (error) {
      Alert.alert('Hata', error.message);
    }
  };

  const pickImage = async () => {
    try {
      const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      
      if (status !== 'granted') {
        Alert.alert(
          'İzin Gerekli',
          'Profil fotoğrafı eklemek için galeriye erişim izni gerekiyor. Ayarlardan izin verebilirsiniz.',
          [
            { text: 'İptal', style: 'cancel' },
            { text: 'Ayarlara Git', onPress: () => Linking.openSettings() }
          ]
        );
        return;
      }

      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [1, 1],
        quality: 0.7,
        allowsMultipleSelection: false,
      });

      if (!result.canceled && result.assets && result.assets[0]) {
        await uploadImage(result.assets[0].uri);
      }
    } catch (error) {
      console.error('Fotoğraf seçilirken hata:', error);
      Alert.alert('Hata', 'Fotoğraf seçilirken bir hata oluştu. Lütfen tekrar deneyin.');
    }
  };

  const uploadImage = async (uri) => {
    try {
      setUploadingImage(true);
      const currentUser = getAuth().currentUser;
      
      if (!currentUser) {
        throw new Error('Kullanıcı oturumu bulunamadı');
      }
      
      // URI'den blob oluştur
      const response = await fetch(uri);
      const blob = await response.blob();
      
      // Storage referansı oluştur
      const imageRef = storageRef(storage, `profileImages/${currentUser.uid}`);
      
      // Fotoğrafı yükle
      await uploadBytes(imageRef, blob);
      
      // Fotoğrafın URL'sini al
      const downloadURL = await getDownloadURL(imageRef);
      
      // Kullanıcı verisini güncelle
      const userRef = ref(database, `users/${currentUser.uid}`);
      await set(userRef, {
        ...username,
        profileImage: downloadURL,
      });
      
      setProfileImage(downloadURL);
      Alert.alert('Başarılı', 'Profil fotoğrafınız başarıyla güncellendi.');
    } catch (error) {
      console.error('Fotoğraf yüklenirken hata:', error);
      Alert.alert(
        'Hata',
        error.message || 'Fotoğraf yüklenirken bir hata oluştu. Lütfen tekrar deneyin.'
      );
    } finally {
      setUploadingImage(false);
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#007AFF" />
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>
        <Text style={styles.title}>Hesap Ayarları</Text>
        
        <View style={styles.profileSection}>
          <TouchableOpacity 
            style={styles.imageContainer} 
            onPress={pickImage}
            disabled={uploadingImage}
          >
            {uploadingImage ? (
              <ActivityIndicator size="large" color="#007AFF" />
            ) : profileImage ? (
              <Image
                source={{ uri: profileImage }}
                style={styles.profileImage}
              />
            ) : (
              <View style={styles.placeholderImage}>
                <Text style={styles.placeholderText}>
                  {username?.charAt(0)?.toUpperCase() || '?'}
                </Text>
              </View>
            )}
            <View style={styles.editBadge}>
              <Text style={styles.editBadgeText}>Düzenle</Text>
            </View>
          </TouchableOpacity>

          <Text style={styles.email}>{username}</Text>
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Kullanıcı Adı</Text>
          <TextInput
            style={styles.input}
            value={username}
            onChangeText={setUsername}
            placeholder="Kullanıcı adınızı girin"
            placeholderTextColor="#9ca3af"
          />
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Yeni Şifre</Text>
          <TextInput
            style={styles.input}
            value={newPassword}
            onChangeText={setNewPassword}
            placeholder="Yeni şifrenizi girin"
            placeholderTextColor="#9ca3af"
            secureTextEntry
          />
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Şifre Tekrar</Text>
          <TextInput
            style={styles.input}
            value={confirmPassword}
            onChangeText={setConfirmPassword}
            placeholder="Şifrenizi tekrar girin"
            placeholderTextColor="#9ca3af"
            secureTextEntry
          />
        </View>

        <TouchableOpacity
          style={[styles.button, styles.updateButton]}
          onPress={handleUpdateProfile}
          disabled={updating}
        >
          {updating ? (
            <ActivityIndicator color="white" />
          ) : (
            <Text style={styles.buttonText}>Profili Güncelle</Text>
          )}
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.button, styles.logoutButton]}
          onPress={handleLogout}
