import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Alert,
  ActivityIndicator,
} from 'react-native';
import { getAuth, updatePassword, signOut } from 'firebase/auth';
import { getDatabase, ref, get, set } from 'firebase/database';
import { database } from '../config/firebase';

const AccountScreen = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);

  useEffect(() => {
    const auth = getAuth();
    const currentUser = auth.currentUser;
    
    if (!currentUser) {
      navigation.navigate('Login');
      return;
    }

    const userRef = ref(database, `users/${currentUser.uid}`);
    get(userRef).then((snapshot) => {
      if (snapshot.exists()) {
        setUsername(snapshot.val().username || '');
      }
      setLoading(false);
    });
  }, [navigation]);

  const handleUpdateProfile = async () => {
    try {
      setUpdating(true);
      const auth = getAuth();
      const currentUser = auth.currentUser;
      
      if (newPassword && confirmPassword) {
        if (newPassword !== confirmPassword) {
          Alert.alert('Hata', 'Şifreler eşleşmiyor');
          return;
        }
        if (newPassword.length < 6) {
          Alert.alert('Hata', 'Şifre en az 6 karakter olmalıdır');
          return;
        }
        await updatePassword(currentUser, newPassword);
      }

      const userRef = ref(database, `users/${currentUser.uid}`);
      await set(userRef, {
        email: currentUser.email,
        username: username,
      });

      Alert.alert('Başarılı', 'Profil güncellendi');
      setNewPassword('');
      setConfirmPassword('');
    } catch (error) {
      Alert.alert('Hata', error.message);
    } finally {
      setUpdating(false);
    }
  };

  const handleLogout = async () => {
    try {
      const auth = getAuth();
      await signOut(auth);
      navigation.navigate('Login');
    } catch (error) {
      Alert.alert('Hata', error.message);
    }
  };

  if (loading) {
    return (
      <View className="flex-1 justify-center items-center bg-surface">
        <ActivityIndicator size="large" color="#007AFF" />
      </View>
    );
  }

  return (
    <View className="flex-1 bg-surface p-4">
      <View className="mb-6">
        <Text className="text-2xl font-bold text-gray-900 mb-4">
          Hesap Ayarları
        </Text>
        
        <View className="mb-4">
          <Text className="text-gray-700 mb-2">Kullanıcı Adı</Text>
          <TextInput
            className="bg-background border border-gray-300 rounded-lg px-4 py-3"
            value={username}
            onChangeText={setUsername}
            placeholder="Kullanıcı adınızı girin"
          />
        </View>

        <View className="mb-4">
          <Text className="text-gray-700 mb-2">Yeni Şifre</Text>
          <TextInput
            className="bg-background border border-gray-300 rounded-lg px-4 py-3"
            value={newPassword}
            onChangeText={setNewPassword}
            placeholder="Yeni şifrenizi girin"
            secureTextEntry
          />
        </View>

        <View className="mb-6">
          <Text className="text-gray-700 mb-2">Şifre Tekrar</Text>
          <TextInput
            className="bg-background border border-gray-300 rounded-lg px-4 py-3"
            value={confirmPassword}
            onChangeText={setConfirmPassword}
            placeholder="Şifrenizi tekrar girin"
            secureTextEntry
          />
        </View>

        <TouchableOpacity
          className="bg-primary rounded-lg py-3 mb-4"
          onPress={handleUpdateProfile}
          disabled={updating}
        >
          {updating ? (
            <ActivityIndicator color="white" />
          ) : (
            <Text className="text-white text-center font-semibold text-lg">
              Profili Güncelle
            </Text>
          )}
        </TouchableOpacity>

        <TouchableOpacity
          className="bg-error rounded-lg py-3"
          onPress={handleLogout}
        >
          <Text className="text-white text-center font-semibold text-lg">
            Çıkış Yap
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default AccountScreen;
