import React, { useState, useEffect } from 'react';
import {
  StyleSheet,
  View,
  Text,
  FlatList,
  TouchableOpacity,
  SafeAreaView,
  ActivityIndicator,
} from 'react-native';
import { ref, get, query, orderByChild, limitToLast, onValue, off, onChildAdded, onChildChanged } from 'firebase/database';
import { database, auth } from '../config/firebase';

const ChatListScreen = ({ navigation }) => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let unsubscribeChats = null;
    let unsubscribeMessages = {};

    const loadChats = async () => {
      try {
        const currentUser = auth.currentUser;
        if (!currentUser) {
          setLoading(false);
          return;
        }

        const chatsRef = ref(database, 'privateChats');
        
        unsubscribeChats = onValue(chatsRef, async (snapshot) => {
          try {
            if (!snapshot.exists()) {
              setChats([]);
              setLoading(false);
              return;
            }

            const chatsData = snapshot.val();
            
            for (const chatId in chatsData) {
              if (chatId.includes(currentUser.uid)) {
                try {
                  const otherUserId = chatId.split('_').find(id => id !== currentUser.uid);
                  const userRef = ref(database, `users/${otherUserId}`);
                  const userSnapshot = await get(userRef);
                  
                  if (userSnapshot.exists()) {
                    const otherUser = {
                      uid: otherUserId,
                      ...userSnapshot.val()
                    };
                    
                    const messagesRef = ref(database, `privateChats/${chatId}/messages`);
                    
                    if (unsubscribeMessages[chatId]) {
                      unsubscribeMessages[chatId]();
                    }

                    const addedUnsubscribe = onChildAdded(messagesRef, (messageSnapshot) => {
                      const message = messageSnapshot.val();
                      updateChatList(chatId, otherUser, message);
                    });

                    const changedUnsubscribe = onChildChanged(messagesRef, (messageSnapshot) => {
                      const message = messageSnapshot.val();
                      updateChatList(chatId, otherUser, message);
                    });

                    unsubscribeMessages[chatId] = () => {
                      addedUnsubscribe();
                      changedUnsubscribe();
                    };

                    const messagesSnapshot = await get(query(messagesRef, limitToLast(1)));
                    if (messagesSnapshot.exists()) {
                      const messages = messagesSnapshot.val();
                      const lastMessage = Object.values(messages)[0];
                      updateChatList(chatId, otherUser, lastMessage);
                    }
                  }
                } catch (error) {
                  console.error('Error processing chat:', error);
                }
              }
            }
          } catch (error) {
            console.error('Error in chat listener:', error);
          } finally {
            setLoading(false);
          }
        });

      } catch (error) {
        console.error('Initial setup error:', error);
        setLoading(false);
      }
    };

    const updateChatList = (chatId, otherUser, message) => {
      setChats(prevChats => {
        const newChats = prevChats.filter(chat => chat.chatId !== chatId);
        const updatedChat = {
          chatId,
          otherUserId: otherUser.uid,
          otherUserEmail: otherUser.email,
          lastMessage: message.text,
          timestamp: message.timestamp,
          isCurrentUser: message.senderId === auth.currentUser.uid
        };
        newChats.push(updatedChat);
        return newChats.sort((a, b) => b.timestamp - a.timestamp);
      });
    };

    loadChats();

    return () => {
      if (unsubscribeChats) {
        unsubscribeChats();
      }
      Object.values(unsubscribeMessages).forEach(unsubscribe => unsubscribe());
    };
  }, []);

  const handleChatPress = (chat) => {
    navigation.navigate('Chat', {
      currentUserId: auth.currentUser.uid,
      otherUserId: chat.otherUserId,
      chatId: chat.chatId,
      otherUserEmail: chat.otherUserEmail
    });
  };

  const handleNewChat = () => {
    navigation.navigate('Users');
  };

  const renderChatItem = ({ item }) => (
    <TouchableOpacity
      style={styles.chatItem}
      onPress={() => handleChatPress(item)}
    >
      <View style={styles.chatInfo}>
        <Text style={styles.userEmail}>{item.otherUserEmail}</Text>
        <Text style={styles.lastMessage} numberOfLines={1}>
          {item.isCurrentUser ? 'Siz: ' : ''}{item.lastMessage}
        </Text>
      </View>
      <Text style={styles.timestamp}>
        {new Date(item.timestamp).toLocaleTimeString('tr-TR', { hour: '2-digit', minute: '2-digit' })}
      </Text>
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#007AFF" />
        <Text style={styles.loadingText}>Sohbetler yükleniyor...</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Sohbetler</Text>
        <TouchableOpacity style={styles.newChatButton} onPress={handleNewChat}>
          <Text style={styles.newChatButtonText}>Yeni Sohbet</Text>
        </TouchableOpacity>
      </View>
      {chats.length === 0 ? (
        <View style={styles.emptyContainer}>
          <Text style={styles.emptyText}>Henüz mesajınız yok</Text>
          <TouchableOpacity 
            style={styles.startChatButton}
            onPress={handleNewChat}
          >
            <Text style={styles.startChatButtonText}>Yeni Sohbet Başlat</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <FlatList
          data={chats}
          renderItem={renderChatItem}
          keyExtractor={(item) => item.chatId}
          contentContainerStyle={styles.listContainer}
        />
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#e5e7eb',
    backgroundColor: '#fff',
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#1f2937',
  },
  newChatButton: {
    backgroundColor: '#007AFF',
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 20,
  },
  newChatButtonText: {
    color: '#fff',
    fontWeight: '600',
  },
  listContainer: {
    padding: 16,
  },
  chatItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    backgroundColor: '#fff',
    borderRadius: 12,
    marginBottom: 8,
    borderWidth: 1,
    borderColor: '#e5e7eb',
  },
  chatInfo: {
    flex: 1,
    marginRight: 12,
  },
  userEmail: {
    fontSize: 16,
    fontWeight: '600',
    color: '#1f2937',
    marginBottom: 4,
  },
  lastMessage: {
    fontSize: 14,
    color: '#6b7280',
  },
  timestamp: {
    fontSize: 12,
    color: '#9ca3af',
  },
  emptyContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  emptyText: {
    fontSize: 18,
    color: '#6b7280',
    marginBottom: 16,
  },
  startChatButton: {
    backgroundColor: '#007AFF',
    paddingHorizontal: 24,
    paddingVertical: 12,
    borderRadius: 24,
  },
  startChatButtonText: {
    color: '#fff',
    fontWeight: '600',
    fontSize: 16,
  },
});

export default ChatListScreen;
