import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  FlatList,
  KeyboardAvoidingView,
  Platform,
  SafeAreaView,
} from 'react-native';
import { ref, push, onValue, off, set } from 'firebase/database';
import { database, auth } from '../config/firebase';

const ChatScreen = ({ route }) => {
  const { currentUserId, otherUserId, chatId, otherUserEmail } = route.params;
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const flatListRef = useRef(null);

  useEffect(() => {
    console.log('Chat screen mounted with params:', { currentUserId, otherUserId, chatId, otherUserEmail });
    
    const messagesRef = ref(database, `privateChats/${chatId}/messages`);
    console.log('Messages reference path:', messagesRef.toString());
    
    const handleData = (snapshot) => {
      const data = snapshot.val();
      console.log('Received messages data:', data);
      
      if (data) {
        const messagesArray = Object.entries(data).map(([id, message]) => ({
          id,
          ...message,
        }));
        console.log('Processed messages array:', messagesArray);
        setMessages(messagesArray);
        setTimeout(() => {
          flatListRef.current?.scrollToEnd({ animated: true });
        }, 100);
      } else {
        console.log('No messages found for this chat');
        setMessages([]);
      }
    };

    onValue(messagesRef, handleData);

    return () => {
      off(messagesRef);
    };
  }, [chatId]);

  const sendMessage = () => {
    if (newMessage.trim()) {
      console.log('Sending message:', newMessage);
      console.log('Chat ID:', chatId);
      console.log('Current user ID:', currentUserId);
      
      const messagesRef = ref(database, `privateChats/${chatId}/messages`);
      const newMessageRef = push(messagesRef);
      
      const messageData = {
        senderId: currentUserId,
        text: newMessage,
        timestamp: Date.now()
      };
      
      console.log('Message data:', messageData);
      
      set(newMessageRef, messageData)
        .then(() => {
          console.log('Message sent successfully');
          setNewMessage('');
          setTimeout(() => {
            flatListRef.current?.scrollToEnd({ animated: true });
          }, 100);
        })
        .catch((error) => {
          console.error('Error sending message:', error);
        });
    }
  };

  const renderMessage = ({ item }) => {
    const isCurrentUser = item.senderId === currentUserId;
    return (
      <View
        className={`max-w-[80%] my-2 p-3 rounded-xl ${
          isCurrentUser 
            ? 'bg-primary self-end' 
            : 'bg-gray-200 self-start'
        }`}
      >
        <Text className="text-xs text-gray-600 mb-1">
          {isCurrentUser ? 'You' : otherUserEmail}
        </Text>
        <Text className={`text-base ${
          isCurrentUser ? 'text-white' : 'text-black'
        }`}>
          {item.text}
        </Text>
        <Text className={`text-xs text-gray-600 self-end mt-1 ${
          isCurrentUser ? 'text-white/80' : 'text-gray-600'
        }`}>
          {new Date(item.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </Text>
      </View>
    );
  };

  return (
    <SafeAreaView className="flex-1 bg-surface">
      <View className="p-4 border-b border-gray-200 bg-background">
        <Text className="text-lg font-bold text-gray-800">
          Chat with {otherUserEmail}
        </Text>
      </View>
      <FlatList
        ref={flatListRef}
        data={messages}
        renderItem={renderMessage}
        keyExtractor={(item) => item.id}
        className="flex-1 p-4"
        inverted={false}
        onContentSizeChange={() => flatListRef.current?.scrollToEnd({ animated: true })}
        onLayout={() => flatListRef.current?.scrollToEnd({ animated: true })}
      />
      <KeyboardAvoidingView
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        className="flex-row p-4 border-t border-gray-200 bg-surface"
      >
        <TextInput
          className="flex-1 border border-gray-300 rounded-full px-4 py-2 mr-2 bg-background max-h-24"
          value={newMessage}
          onChangeText={setNewMessage}
          placeholder="Type a message..."
          multiline
        />
        <TouchableOpacity 
          className="bg-primary rounded-full px-6 py-2 justify-center items-center"
          onPress={sendMessage}
        >
          <Text className="text-white font-semibold">Send</Text>
        </TouchableOpacity>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

export default ChatScreen;
