import React, { useState, useEffect, useCallback } from 'react';
import {
  StyleSheet,
  View,
  Text,
  FlatList,
  TouchableOpacity,
  SafeAreaView,
  ActivityIndicator,
  Image,
} from 'react-native';
import { ref, get, query, orderByChild, limitToLast, onValue, off } from 'firebase/database';
import { database, auth } from '../config/firebase';

const ChatListScreen = ({ navigation }) => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [unsubscribers, setUnsubscribers] = useState({});

  useEffect(() => {
    let isMounted = true;
    let chatUnsubscriber = null;

    const loadChats = async () => {
      try {
        const currentUser = auth.currentUser;
        if (!currentUser) {
          navigation.replace('Login');
          setLoading(false);
          return;
        }

        const chatsRef = ref(database, 'privateChats');
        
        chatUnsubscriber = onValue(chatsRef, async (snapshot) => {
          if (!isMounted) return;

          try {
            if (!snapshot.exists()) {
              setChats([]);
              setLoading(false);
              return;
            }

            const chatsData = snapshot.val();
            const newChats = [];
            const newUnsubscribers = {};
            
            for (const chatId in chatsData) {
              if (chatId.includes(currentUser.uid)) {
                try {
                  const otherUserId = chatId.split('_').find(id => id !== currentUser.uid);
                  const userRef = ref(database, `users/${otherUserId}`);
                  const userSnapshot = await get(userRef);
                  
                  if (userSnapshot.exists()) {
                    const otherUser = {
                      uid: otherUserId,
                      ...userSnapshot.val()
                    };
                    
                    const messagesRef = ref(database, `privateChats/${chatId}/messages`);
                    const messagesSnapshot = await get(query(messagesRef, limitToLast(1)));
                    
                    if (messagesSnapshot.exists()) {
                      const messages = messagesSnapshot.val();
                      const lastMessage = Object.values(messages)[0];
                      
                      newChats.push({
                        chatId,
                        otherUserId: otherUser.uid,
                        otherUserEmail: otherUser.email,
                        otherUserProfileImage: otherUser.profileImage,
                        lastMessage: lastMessage.text,
                        timestamp: lastMessage.timestamp,
                        isCurrentUser: lastMessage.senderId === currentUser.uid
                      });
                    }
                  }
                } catch (error) {
                  console.error('Error processing chat:', error);
                }
              }
            }

            if (isMounted) {
              setChats(newChats.sort((a, b) => b.timestamp - a.timestamp));
              setUnsubscribers(newUnsubscribers);
            }
          } catch (error) {
            console.error('Error in chat listener:', error);
          } finally {
            if (isMounted) {
              setLoading(false);
            }
          }
        });
      } catch (error) {
        console.error('Initial setup error:', error);
        if (isMounted) {
          setLoading(false);
        }
      }
    };

    loadChats();

    return () => {
      isMounted = false;
      if (chatUnsubscriber) {
        chatUnsubscriber();
      }
      Object.values(unsubscribers).forEach(unsubscribe => unsubscribe());
    };
  }, [navigation]);

  const handleChatPress = useCallback((chat) => {
    navigation.navigate('Chat', {
      currentUserId: auth.currentUser.uid,
      otherUserId: chat.otherUserId,
      chatId: chat.chatId,
      otherUserEmail: chat.otherUserEmail
    });
  }, [navigation]);

  const handleNewChat = useCallback(() => {
    navigation.navigate('Users');
  }, [navigation]);

  const renderChatItem = useCallback(({ item }) => (
    <TouchableOpacity
      style={styles.chatItem}
      onPress={() => handleChatPress(item)}
    >
      <View style={styles.avatarContainer}>
        {item.otherUserProfileImage ? (
          <Image
            source={{ uri: item.otherUserProfileImage }}
            style={styles.avatar}
            onError={(e) => console.log('Error loading image:', e.nativeEvent.error)}
          />
        ) : (
          <View style={[styles.avatar, styles.placeholderAvatar]}>
            <Text style={styles.placeholderText}>
              {item.otherUserEmail?.charAt(0)?.toUpperCase() || '?'}
            </Text>
          </View>
        )}
      </View>
      <View style={styles.chatInfo}>
        <Text style={styles.userEmail}>{item.otherUserEmail}</Text>
        <Text style={styles.lastMessage} numberOfLines={1}>
          {item.isCurrentUser ? 'Siz: ' : ''}{item.lastMessage}
        </Text>
      </View>
      <Text style={styles.timestamp}>
        {new Date(item.timestamp).toLocaleTimeString('tr-TR', { hour: '2-digit', minute: '2-digit' })}
      </Text>
    </TouchableOpacity>
  ), [handleChatPress]);

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#007AFF" />
        <Text style={styles.loadingText}>Sohbetler yükleniyor...</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Sohbetler</Text>
        <TouchableOpacity style={styles.newChatButton} onPress={handleNewChat}>
          <Text style={styles.newChatButtonText}>Yeni Sohbet</Text>
        </TouchableOpacity>
      </View>
      {chats.length === 0 ? (
        <View style={styles.emptyContainer}>
          <Text style={styles.emptyText}>Henüz mesajınız yok</Text>
          <TouchableOpacity 
            style={styles.startChatButton}
            onPress={handleNewChat}
          >
            <Text style={styles.startChatButtonText}>Yeni Sohbet Başlat</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <FlatList
          data={chats}
          renderItem={renderChatItem}
          keyExtractor={(item) => item.chatId}
          contentContainerStyle={styles.listContainer}
        />
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#e5e7eb',
    backgroundColor: '#fff',
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#1f2937',
  },
  newChatButton: {
    backgroundColor: '#007AFF',
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 20,
  },
  newChatButtonText: {
    color: '#fff',
    fontWeight: '600',
  },
  listContainer: {
    padding: 16,
  },
  chatItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 12,
    backgroundColor: '#fff',
    borderRadius: 12,
    borderWidth: 1,
    borderColor: '#e5e7eb',
    marginBottom: 8,
  },
  avatarContainer: {
    marginRight: 12,
  },
  avatar: {
    width: 50,
    height: 50,
    borderRadius: 25,
  },
  placeholderAvatar: {
    backgroundColor: '#e5e7eb',
    justifyContent: 'center',
    alignItems: 'center',
  },
  placeholderText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#9ca3af',
  },
  chatInfo: {
    flex: 1,
    marginRight: 12,
  },
  userEmail: {
    fontSize: 16,
    fontWeight: '600',
    color: '#1f2937',
    marginBottom: 4,
  },
  lastMessage: {
    fontSize: 14,
    color: '#6b7280',
  },
  timestamp: {
    fontSize: 12,
    color: '#9ca3af',
  },
  emptyContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  emptyText: {
    fontSize: 18,
    color: '#6b7280',
    marginBottom: 16,
  },
  startChatButton: {
    backgroundColor: '#007AFF',
    paddingHorizontal: 24,
    paddingVertical: 12,
    borderRadius: 24,
  },
  startChatButtonText: {
    color: '#fff',
    fontWeight: '600',
    fontSize: 16,
  }
});

export default ChatListScreen;
